import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import {Link} from 'react-router-dom'

import {IoSearch} from 'react-icons/io5'

import NotificationDropdown from '../notification/NotificationDropdown'
import RsMenu from './RsMenu'
import SearchResult from './SearchResult'
import OutsideClick from '../util/OutsideClick'
import Logo from '../../assets/logo.png'
import { motion, AnimatePresence } from "framer-motion"

import styles from './RsHeader.module.css'

const RsHeader = () => {
    const [showMenu, setShowMenu] = useState(false)
    const [showSearch, setShowSearch] = useState(false)
    const [showNoti, setShowNoti] = useState(false)

    const [notiLength, setNotiLength] = useState()

    const {auth, notice} = useSelector(state=>state)

    useEffect(()=>{
        const readNotices = notice.data.filter(noti=>noti.isRead.every(id=>id!=auth.user._id));
        setNotiLength(readNotices.length)
    },[notice])

    return (
        <div className={styles.header}>
            <div className={styles.container}>
                <div className={styles.logo_wrapper}>
                    <Link to='/' >
                        <img className={styles.logo} src={Logo} alt='logo'/>
                    </Link>
                </div>               
                {/* <div className={styles.btn_group_wrapper}> */}
                    <div className={styles.btn_group}>
                        <div onClick={()=>setShowSearch(!showSearch)} className={styles.search_btn}>
                            <IoSearch/>
                        </div>
                        <div onClick={()=>setShowNoti(!showNoti)} className={styles.noti_btn}>
                            {notiLength}
                        </div>
                        <div onClick={()=>setShowMenu(!showMenu)} className={styles.menu_btn}>
                            <div className={styles.menu_icon}>
                                <div style={showMenu ? {left: '-100%', opacity: 0} : {}} className={styles.line_1}></div>
                                <div style={showMenu ? {transformOrigin: 'left', transform: 'rotateZ(45deg)'} : {}} className={styles.line_2}></div>
                                <div style={showMenu ? {transformOrigin: 'left', transform: 'rotateZ(-45deg)'} : {}} className={styles.line_3}></div>
                            </div>
                        {/* </div> */}
                    </div>
                </div>              
            </div>
            <AnimatePresence >
                {showMenu &&
                <motion.div 
                key='menu'
                initial={{transform: 'translate(50%,0)'}}
                animate={{transform: 'translate(0,0)', transitionDuration: `.2s`}}
                exit={{transform: 'translate(50%,0)'}}
                >
                    <OutsideClick state={showMenu} setState={setShowMenu}>
                        <RsMenu/>
                    </OutsideClick>
                </motion.div>}

                {showSearch &&
                <motion.div
                key='search'
                initial={{transform: 'translate(0,-100%)'}}
                animate={{transform: 'translate(0,0)', transitionDuration: `.2s`}}
                exit={{transform: 'translate(0,-100%)'}}
                className={styles.result}
                >
                    <OutsideClick state={showSearch} setState={setShowSearch}>
                        <SearchResult/>
                    </OutsideClick>    
                </motion.div>}

                {showNoti &&
                <motion.div
                key='notification'
                className={styles.noti}
                >
                    <OutsideClick state={showNoti} setState={setShowNoti}>
                        <NotificationDropdown isShow={showNoti} setIsShow={setShowNoti}/>
                    </OutsideClick>    
                </motion.div>}
            </AnimatePresence >
        </div>
    )
}

export default RsHeader
