import React from 'react';
import styles from './SlideForm.module.css';
import RoundButton from '../Util/RoundButton';
import {useDispatch,useSelector} from 'react-redux';
import {slideActions} from '../../store/index';

export default function SlideForm() {
    const dispatch = useDispatch();
    const isLogin = useSelector(state => state.isLogin);
    console.log(isLogin);
    const toggleSlide = () => {
        dispatch(slideActions.toggleSlide());
    }

    return (
        <div className={`${styles.slide} ${!isLogin && styles.slide_animation}`}>
            <RoundButton onClick={toggleSlide()} color={'#487fec'} bgcolor={'white'} type={'text'} value={'Xin chao'} />
        </div>
    )
}
