import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { motion } from 'framer-motion'

import Header from '../../components/header/Header'
import ProjectManager from '../../components/code/ProjectManager'
import SourceCode from '../../components/code/SourceCode'

import styles from './Project.module.css'

const Code = () => {
    // const constRef = useRef(null);
    const [firstTab, setFirstTab] = useState(true);

    return (
        <>
            <Header/>
            {/* <motion.div style={{width: 500, height:500}} ref={constRef}>
                <motion.div
                drag
                dragConstraints={constRef}
                style={{width: 'fit-content'}}
                >
                    drag me
                </motion.div>{
            </motion.div> */}
            <div className={styles.container}>
                <div>
                    <div className={styles.navigation}>
                        <div onClick={()=>setFirstTab(true)} className={`${styles.tab} ${firstTab ? styles.active:''}`}>Projects manager</div>
                        <div onClick={()=>setFirstTab(false)} className={`${styles.tab} ${!firstTab ? styles.active:''}`}>Source code</div>
                    </div>
                </div>
                {firstTab ? <ProjectManager/> : <SourceCode/>}
            </div>
        </>
    )
}

export default Code
