import React, { useEffect, useState } from 'react'
import {useParams, Link} from 'react-router-dom';
import styles from './Profile.module.css';
import Header from '../../components/header/Header';
import RoundWrapper from '../../components/util/RoundWrapper';
import { useDispatch, useSelector } from 'react-redux';
import { getUser } from '../../store/actions/profileAction';
import UserCard from '../../components/util/UserCard';
import FollowBtn from '../../components/profile/FollowBtn';
import Status from '../../components/home/Status'
import PostCard from '../../components/post/PostCard';

const Profile = () => {   
    const dispatch = useDispatch();
    
    const {id} = useParams();
    const auth = useSelector(state=> state.auth);
    const profile = useSelector(state=> state.profile);

    const [isFollowing, setIsFollowing] = useState(true);
    const [userData, setUserData] = useState({});
    const [isMe, setIsMe] = useState(false);

    useEffect(() => {
        if(profile.user._id === auth.user._id) setIsMe(true);
        else setIsMe(false);
    }, [auth, profile]);

    useEffect(() => {
        dispatch(getUser(auth,id));

    },[auth, dispatch, id])

    useEffect(()=>{
        if(id === auth.user._id) setUserData(auth.user);
        else {
            setUserData(profile.user);
        }
    },[profile]);


    return (
        <>
            <Header/>
            <div className={styles.profile_wrapper}>
                <div className={styles.leftside_bar}>
                    <div className={styles.heading}>
                        <div onClick={()=> setIsFollowing(!isFollowing)} className={styles.title}>{isFollowing ? ' Following ' : 'Followers'}</div>
                        <div className={styles.value}>
                            {isFollowing ? (!profile.user.following ? 0 : profile.user.following.length) : (!profile.user.followers ? 0 : profile.user.followers.length)}
                        </div>
                    </div>
                    <div className={styles.list_items}>
                        <div className={`${styles.following_bar} ${!isFollowing && styles.translate_left}`}>
                            {profile.user.following && profile.user.following.map(user => 
                                <div key={user._id} className={styles.item_wrapper}>
                                    <Link to={`/profile/${user._id}`} className={styles.item}>
                                        <UserCard avatar={user.avatar} username={user.username} />
                                    </Link>
                                    {user._id !== auth.user._id && (
                                        <div className={styles.btn_wrapper}>
                                            <FollowBtn user={user}/>
                                        </div>
                                    )}
                                </div>  
                            )}                                   
                        </div>
                        <div className={`${styles.follower_bar} ${!isFollowing && styles.translate_left}`}>
                            {profile.user.followers &&profile.user.followers.map(user => 
                                <div key={user._id} className={styles.item_wrapper}>
                                    <Link to={`/profile/${user._id}`} className={styles.item}>
                                        <UserCard user={profile.user} avatar={user.avatar} username={user.username} />
                                    </Link>
                                    {user._id !== auth.user._id && (
                                        <div className={styles.btn_wrapper}>
                                            <FollowBtn user={user}/>
                                        </div>
                                    )}                                   
                                </div>  
                            )} 
                        </div>
                    </div>
                </div>
                <div className={styles.container}>
                    <div className={styles.profile}>
                        <div style={{backgroundImage: `url('${profile.user.background}')`}} className={styles.user_bg_image}>
                            <div className={styles.user_avatar_image}>
                                <RoundWrapper diameter='250px'>
                                    <img src={profile.user.avatar} alt='avatar' />
                                </RoundWrapper>
                            </div>
                        </div>
                        {/* info */}
                        <div className={styles.information}>
                            <div className={styles.introduce}>
                                <div className={styles.name}>{profile.user.username}</div>
                                <div className={styles.bios}>{profile.user.bio}</div>
                            </div>
                            <div className={styles.follow_btn}>
                                <FollowBtn  user={profile.user} />
                            </div>
                        </div>
                        {/* status */}
                        {profile.user._id === auth.user._id && (
                            <div className={styles.status_wrapper}>
                                <Status />
                            </div>
                        )}
                        {/* posts */}
                        <PostCard/>
                    </div>
                </div>
            </div>
        </>   
    )
}

export default Profile
