import React, { useState,useRef, useEffect, useMemo } from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {Link} from 'react-router-dom'

import {AiOutlineSearch} from 'react-icons/ai'

import { searchUser } from '../../store/actions/searchAction'

import UserCard from '../util/UserCard'
import { motion, AnimatePresence } from "framer-motion"
import { debounce } from 'lodash'

import Spinner from '../../assets/Spinner.png'
import styles from './Search.module.css'

const Search = () => {
    const dispatch = useDispatch();
    
    const ref = useRef();
    const {auth, search} = useSelector(state=>state);

    const [data, setData] = useState('');
    const [users, setUsers] = useState([]);
    const [isShow, setIsShow] = useState(false);

    useEffect(()=>{
        const checkIfClickOutside = e => {
            if(isShow && ref.current && !ref.current.contains(e.target)) setIsShow(false); 
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    useEffect(()=>{
        if(!data) {
            setUsers([]);
            setIsShow(false);
        }        
    },[data])

    useEffect(()=>{
        setUsers(search.users)
    },[search.users])

    const submitHandler = async (userInput) => {
        if(!data.trim()) return;
        await dispatch(searchUser(data, auth))
        setIsShow(true);
    }

    const debouncedChangeHandler = useMemo(
        () => debounce((userInput) => submitHandler(userInput), 500),
        [data]
    );

    useEffect(() => {
    return () => {
        debouncedChangeHandler.cancel();
    };
    }, [debouncedChangeHandler]);

    const inputHandler = e => {
        debouncedChangeHandler(e.target.value);
        setData(e.target.value);
    }

    return (
        <div ref={ref}  className={styles.search_bar}>
            <div onClick={()=>setIsShow(true)} className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input onFocus={submitHandler} spellCheck="false" onChange={inputHandler} autoComplete="off" value={data}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
            </div>
            <AnimatePresence>
                {isShow && 
                <motion.div
                initial={{maxHeight: 0, overflow: 'hidden'}}
                animate={{maxHeight: 400, overflow: 'overlay'}}
                transition={{duration: .5}}
                exit={{ maxHeight: 0, overflow: 'hidden'}}
                className={styles.results}>            
                    {search.loading ?
                    <div className={styles.spinner_wrapper}><img src={Spinner} alt='spinner' /></div>
                    :
                    users.length==0 ? 
                    <div className={styles.no_result}>No searching result...</div>
                    :
                    users.map(user => 
                        <div key={user._id} className={styles.item_wrapper}>
                            <Link to={`/profile/${user._id}`} className={styles.item}>
                                <UserCard height='2rem' avatar={user.avatar} username={user.username} />
                            </Link>             
                        </div>
                    )}
                </motion.div>}
            </AnimatePresence>
        </div>
    )
}

export default Search
