import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { getAPI } from '../../apis/Axios'

import PostCard from '../post/PostCard'

import InfiniteScroll from 'react-infinite-scroll-component'

const InfiniteList = ({posts}, elem) => {
    const dispatch = useDispatch();
    const {auth} = useSelector(state=>state);

    const [page, setPage] = useState(1);
    const [load, setLoad] = useState(false);

    const getData = () => {
        setPage(page+1);
        setLoad(true);
    }

    useEffect( async ()=>{
        if(load) {
            const res = await getAPI(`post?page=${page}`, auth.token)
            if(res.data.posts.length===0) {
                setLoad(false)
                return;
            }
            dispatch({type: 'GET_MORE_POSTS', payload: res.data.posts})
            setLoad(false)
        }
    },[load])

    return (
        <InfiniteScroll
            dataLength={posts.length}
            next={getData}
            hasMore={true}
            scrollableTarget={elem}
            style={{padding: '0 70px', margin: '0 -70px'}}
        >
            {posts.length>0?posts.map(post=>(
                <PostCard key={post._id} post={post} />
            ))
            :
                <div>No post available</div>
            }
        </InfiniteScroll>
            
        
    )
}

export default InfiniteList
