const initialState = {
    user: {},
    posts: [],
    loading: false
}

const profileReducer = (state = initialState, action) => {
    switch (action.type){
        case 'GET_USER':
            return {
                ...state,
                user: action.payload
            };
        case 'FOLLOW':
            return {
                ...state,
                user: action.payload
            };
        case 'UNFOLLOW':
            return {
                ...state,
                user: action.payload
            };
        case 'GET_USER_POSTS':
            return {
                ...state,
                posts: action.payload
            }
        case 'UPDATE_USER_POST':
            return {
                ...state,
                posts: action.payload
            }
        default:
            return state;
    }
}


export default profileReducer;
