import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom';
import RoundWrapper from '../util/RoundWrapper';
import styles from './NotificationDisplay.module.css'

const NotificationDisplay = () => {
    const {notice} = useSelector(state=>state);
    const [isShow, setIsShow] = useState(false);
    const [msg, setMsg] = useState({});

    useEffect(()=>{
        if(!notice.popup) return;
        setIsShow(true);
        setMsg(notice.data[0]);

    },[notice])

    return (
        <>
            {isShow && 
            <div className={styles.notice_wrapper}>
                <div className="container">
                    <Link>
                        <div className="heading">
                            <div className="tilte"></div>
                            <div className="close"></div>
                        </div>
                        <div className="content">
                            <div className="avatar">
                                <RoundWrapper diameter='60px'>
                                    <img src={msg.user.avatar} alt="avatar"/>
                                </RoundWrapper>
                            </div>
                            <div className="body"></div>
                            <div className="image"></div>
                        </div>
                    </Link>
                </div>
            </div>
            }
        </>
    )
}

export default NotificationDisplay
