import React, {useState, useDispatch} from 'react'
import {useSelector} from 'react-redux';
import Logo from '../../../assets/logo.png';
import RoundWrapper from '../../util/RoundWrapper';
import styles from './InputComment.module.css'

const InputComment = ({onReply, setOnReply, post}) => {
    const dispatch = useDispatch();
    const [body, setBody] = useState('');
    const {auth} = useSelector(state=>state);

    const newComment = {
        body,
        user: auth.user,
        createdAt: new Date().toISOString(),
        reply: onReply && onReply.commentId,
        tag: onReply && onReply.user
    }

    const inputHandler = (e) => {
        setBody(e.target.value);
    }
    
    return (
        <div className={styles.container}>
            <div className={styles.avatar}>
                <RoundWrapper diameter='50px' >
                    <img src={Logo} />
                </RoundWrapper>
            </div>
            <div className={styles.input_wrapper}>
                <textarea onChange={inputHandler} value={body} spellCheck="false" rows='1' type="text" name="body" placeholder="Write your comment here..." className={styles.textarea} />
            </div>
        </div>
    )
}

export default InputComment
