import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import styles from './EditProjectModal.module.css'

const EditProjectModal = () => {
    const dispatch = useDispatch()

    const {auth} = useSelector(state=>state)

    const [name, setName] = useState('')

    const submitHandler = () => {
        const body = {name}
        dispatch(createProject(auth, body))
    }

    return (
        <div className={styles.container}>
            <div className={styles.title}>Rename Project</div>
            <div className={styles.input_wrapper}>
                <label htmlFor="name" >Project's Name: </label>
                <input spellCheck="false" placeholder='Name...' id='name' type='text' name='name' value={name} onChange={e=>setName(e.target.value)} className={styles.input}/>
            </div>
            <button onClick={submitHandler} className={styles.submit}>Submit</button>
        </div>
    )
}

export default EditProjectModal
