const initialState = {
    code: '',
    html: '',
    css: '',
    js: '',
    project: {},
    tab: 0
}

const codeReducer = (state=initialState, action) => {
    switch (action.type) {
        case 'OPEN_PROJECT':
            return {
                ...state,
                project: action.payload
            }
        case 'OPEN_CODE':
            return {
                ...state,
                code: action.payload.code,
                html: action.payload.html,
                css: action.payload.css,
                js: action.payload.js,
                tab: 1
            }
        case 'SAVE_CODE':
            return {
                ...state,
                code: action.payload.code
            }
        case 'SAVE_HTML':
            return {
                ...state,
                html: action.payload
            }
        case 'SAVE_CSS':
            return {
                ...state,
                css: action.payload
            }
        case 'SAVE_JS':
            return {
                ...state,
                js: action.payload
            }
        case 'SET_TAB':
            return {
                ...state,
                tab: action.payload
            }
        default: return state;
    }
}

export default codeReducer;
