import React, { useEffect, useRef, useState } from 'react'
import {GoPrimitiveDot} from 'react-icons/go'
import {CgMoreAlt} from 'react-icons/cg'
import { Link } from 'react-router-dom'
import RoundWrapper from '../../util/RoundWrapper'
import NoticeOption from './NoticeOption'
import styles from './NoticeCard.module.css'
import moment from 'moment'
import TagIcon from '../../util/TagIcon'

const NoticeCard = ({msg}) => {
    const ref = useRef();
    const [isShowOption, setShowOption] = useState(false);

    useEffect(()=>{
        // Function to check if click outside the object
        const checkIfClickOutside = e => {
            if(isShowOption && ref.current &&!ref.current.contains(e.target)) setShowOption(false); 
        }
        // Set event 'when click' trigger
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            // Clean the event
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShowOption]);

    return (
    <div className={styles.container}>
        <Link to={`post/${msg.url}`} key={msg.id} >
            <div className={styles.notice_wrapper}>
                <div className={styles.avatar}>
                    <RoundWrapper diameter='60px'>
                        <img src={msg.user.avatar} alt="avatar"/>
                        <div className={styles.tag_icon_wrapper}>
                            <TagIcon tag={msg.tag} />
                        </div>
                    </RoundWrapper>
                    
                </div>
                <div className={styles.body}>
                    <div className={styles.text_noti}>
                        <b>{msg.user.username}</b> {msg.text}
                    </div>
                    <div style={{color: `${msg.isRead ? 'inherit' : '#1880bd'}`}} className={styles.time}>{moment(msg.createdAt).fromNow()}</div>
                </div>
                {msg.image && <div className={styles.image_wrapper}><img src={msg.image}/></div>}
                {!msg.isRead && <div className={styles.dot}><GoPrimitiveDot /></div>}
                            
                
            </div>
        </Link>
        <div ref={ref} onClick={()=>setShowOption(!isShowOption)} className={styles.notice_option_wrapper}>
                    <div style={{opacity: `${isShowOption ? '1' : '0'}`}} className={styles.notice_option}>
                        <CgMoreAlt className={styles.icon}/>
                    </div>
            </div>
            <NoticeOption msg={msg} isShow={isShowOption} />

        </div>
    )
}

export default NoticeCard
