import React, { useEffect, useState } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import styles from './Search.module.css';
import {AiOutlineSearch} from 'react-icons/ai';
import { getAPI } from '../../apis/Axios';
import { CSSTransition } from "react-transition-group";
import UserCard from './UserCard';

const Search = () => {
    const dispatch = useDispatch();
    const auth = useSelector(state=>state.auth);

    const [search,setSearch] = useState('');
    const [users,setUsers] = useState([]);

    const [data,setData] = useState({
        search: '',
        users: []
    })

    const submitHandler = async () => {
        if(!data.search) return;
        if(auth.token && data.search){
            try {
                const res = await getAPI(`user/search?username=${search}`, auth.token);
                // setUsers(res.data.user);
                setData({search: data.search, users: res.data.user});

            } catch (err) {
                dispatch({
                type: 'ALERT', payload: {error: err.response.data.msg}
            })
            }   
        }
    }

    // const inputHandler = e => setSearch(e.target.value);
    const searchHandler = e =>{
        setData({search: e.target.value, users: data.users,});
        console.log(data.search)
    } 

    const onChangeHandler = e => {
        searchHandler(e);
        console.log(data.search)
        submitHandler();
        // inputHandler(e);
        // if(!search) setUsers([]);
    }

    return (
        <div className={styles.search_bar}>
            <div className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input onChange={onChangeHandler} autoComplete="off" value={data.search}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
            </div>
            <CSSTransition
            in={data.search}
            timeout={400}
            classNames={{
                enter: styles.enter,
                enterActive: styles.enter_active,
                exit: styles.exit,
                exitActive: styles.exit_active,
            }}
            unmountOnExit
            >
                <div className={styles.results} >
                    <div className={styles.list_items}>
                        {  users.map(user => 
                            <UserCard key={user.id} username={user.username} />
                        )}
                    </div>
                </div> 
            </CSSTransition>
        </div>
    )
}

export default Search
