const initialState = {
    code: '',
    html: '',
    css: '',
    js: '',
    tab: 1
}

const codeReducer = (state=initialState, action) => {
    switch (action.type) {
        case 'OPEN_CODE':
            return {
                ...state,
                code: action.payload.code,
                html: action.payload.html,
                css: action.payload.css,
                js: action.payload.js,
                tab: 2
            }
        case 'SAVE_CODE':
            return {
                ...state,
                code: action.payload.code,
                html: action.payload.html,
                css: action.payload.css,
                js: action.payload.js,
            }
        case 'SET_TAB':
            return {
                ...state,
                tab: action.payload
            }
        default: return state;
    }
}

export default codeReducer;
