import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { deletePost } from '../../store/actions/postAction';
import {MdModeEdit, MdSave} from 'react-icons/md'
import {RiDeleteBinLine} from 'react-icons/ri'
import styles from './PostOption.module.css'
import ToggleCard from '../util/ToggleCard';

const PostOption = ({setIsEdit, post}) => {
    const dispatch = useDispatch();
    const {auth, profile, socket} = useSelector(state=>state);
    const [isMe, setIsMe] = useState(false);

    useEffect(()=>{
        if(auth.user._id === post.user._id) setIsMe(true);
        else setIsMe(false);
    },[auth.user._id, post.user._id]);

    const deleteHander = () => {
        dispatch(deletePost(auth, post._id, profile, socket));
    }

    return (
        <ToggleCard>
            <div className={styles.option_item} onClick={()=>console.log('dog')} >
                <MdSave className={styles.icon}/>
                Save
            </div>
            {isMe && 
            <div className={styles.option_item} onClick={()=>setIsEdit(true)}>
                <MdModeEdit className={styles.icon}/>
                Edit
            </div>
            }
            {isMe && 
            <div className={styles.option_item} onClick={deleteHander} >
                <RiDeleteBinLine className={styles.icon}/>
                Delete
            </div>
            } 
        </ToggleCard>       
    )
}

export default PostOption
