import React from 'react'
import styles from './DropdownCard.module.css'

const DropdownCard = (props) => {
    return (
        <div style={{bottom: props.bottom, right: props.right, width: props.width, maxHeight: `${props.isShow?'1000px':'0px'}`}} className={styles.container}>
            {props.children}
        </div>
    )
}

export default DropdownCard
