import React, { useState,useRef, useEffect } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {Link} from 'react-router-dom';

import {AiOutlineSearch} from 'react-icons/ai';

import { searchUser } from '../../store/actions/searchAction';

import { CSSTransition,TransitionGroup } from "react-transition-group";
import UserCard from '../util/UserCard';

import styles from './Search.module.css';

const Search = () => {
    const dispatch = useDispatch();
    
    const ref = useRef();
    const {auth, search} = useSelector(state=>state);

    const [data,setData] = useState('');
    const [users,setUsers] = useState([]);
    const [isShow,setIsShow] = useState(false);

    useEffect(()=>{
        const checkIfClickOutside = e => {
            if(isShow && ref.current && !ref.current.contains(e.target)) setIsShow(false); 
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    useEffect(()=>{
        if(!data) {
            setUsers([]);
            setIsShow(false);
        }
        else submitHandler();
        
    },[data])

    const submitHandler = async () => {
        // e.preventDefault();
        if(!data) return;
        dispatch(searchUser(data, auth))
        setIsShow(true);
    }

    const inputHandler = e => {
        setData(e.target.value);
    }

    return (
        <div ref={ref}  className={styles.search_bar}>
            <div onClick={()=>setIsShow(true)} className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input spellCheck="false" onChange={inputHandler} autoComplete="off" value={data}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
            </div>
                {isShow && 
                <div className={styles.results}>            
                    {search.loading ?
                    <div>loading</div>
                    :
                    search.users.map(user => 
                        <div key={user._id} className={styles.item_wrapper}>
                            <Link to={`/profile/${user._id}`} className={styles.item}>
                                <UserCard height='2rem' avatar={user.avatar} username={user.username} />
                            </Link>             
                        </div>
                    )}
                </div>}
        </div>
    )
}

export default Search
